"use strict";

var burger = document.querySelector(".burger"),
    WIDTH = (burger.addEventListener("click", function () {
  return burger.classList.toggle("active");
}), window.addEventListener("resize", function () {
  540 < this.innerWidth && burger.classList.remove("active");
}), 1600);

function changeWidthSlider() {
  (WIDTH = window.document.body.clientWidth) <= 1600 ? document.querySelector(".startScreen__slider").style.width = "".concat(WIDTH, "px") : WIDTH = 1600;
}

changeWidthSlider(), window.addEventListener("resize", function () {
  changeWidthSlider();
});
var n = document.querySelectorAll(".startScreen__slider-item").length - 1,
    track = document.querySelector(".startScreen__track"),
    btnPrev = document.querySelector(".startScreen__slider-nav__btn-prev"),
    btnNext = document.querySelector(".startScreen__slider-nav__btn-next");
btnPrev.addEventListener("click", function (e) {
  console.log(track.offsetLeft, WIDTH), track.offsetLeft < 0 && (track.style.left = "".concat(parseInt(track.style.left) + WIDTH, "px"));
}), btnNext.addEventListener("click", function (e) {
  var t = parseInt(track.style.left) ? parseInt(track.style.left) : 0;
  -WIDTH * n < t && (parseInt(track.style.left) ? track.style.left = "".concat(t - WIDTH, "px") : track.style.left = "".concat(-WIDTH, "px"));
});